SELECT        dbo.JY64900V.EKOMNR AS KOMMUNENUMMER, dbo.JY64900V.EEJDNR AS EJENDOMSNUMMER, RTRIM(dbo.JY64800V.ABELIG) AS EJENDOMSBELIGGENHED, 
                         CAST(RTRIM(RTRIM(dbo.JY67700V.VEJ_ADRESSENAVN) + ' ' + REPLACE(LTRIM(REPLACE(RTRIM(dbo.JY67700V.HUS_NUMMER), '0', ' ')), ' ', '0')) AS VARCHAR(50)) 
                         AS MATRIKELADRESSE, RTRIM(dbo.JY64900V.ANAVN) AS EJERS_NAVN, RTRIM(dbo.JY64900V.ACONAVN) AS EJERS_CO_NAVN, RTRIM(dbo.JY64900V.AADRESS) 
                         AS EJERS_ADRESSE, RTRIM(dbo.JY64900V.AUDVADR) AS EJERS_UDVIDEDE_ADRESSE, RTRIM(dbo.JY64900V.APOST) AS POSTDISTRIKT, 
                         dbo.JY64800V.CBENYT AS BENYTTELSESKODE, RTRIM(CAST(dbo.QL61500V.TEKST AS VARCHAR(70))) AS BENYTTELSE, 
                         dbo.JY64900V.CMSTAT AS EJERS_STATUSKODE, RTRIM(CAST(dbo.QL63200V.TEKST AS VARCHAR(75))) AS EJERSTATUS, RTRIM(dbo.JY64900V.CPENS) 
                         AS PENSIONISTKODE, RTRIM(CAST(dbo.QL63400V.TEKST AS VARCHAR(75))) AS PENSIONISTSTATUS, RTRIM(dbo.JY64900V.CLEJE) AS LEJEVAERDIKODE, 
                         RTRIM(CAST(dbo.QL63500V.TEKST AS VARCHAR(75))) AS LEJEVAERDI, RTRIM(dbo.JY64900V.CBSKADR) AS KODE_FOR_ADRESSEBESKYTTELSE, 
                         RTRIM(CAST(dbo.QL63600V.TEKST AS VARCHAR(75))) AS ADRESSEBESKYTTELSE, dbo.JY64900V.DSLUTS AS SLUTSEDDELDATO, 
                         dbo.JY64900V.DSKOED AS SKOEDEDATO, dbo.JY64900V.DOVTG AS OVERTAGELSESDATO, RTRIM(CAST(dbo.QL63700V.TEKST AS VARCHAR(75))) 
                         AS OVERDRAGELSESMAADE, dbo.JY64900V.BKOEBS AS KOEBESUM, dbo.JY64900V.DCPRCIR AS EJER_CPR_CVR, dbo.JY64800V.VEJ_KODE AS VEJKODE, 
                         RTRIM(dbo.JY64800V.HUS_NUMMER) AS HUSNUMMER_BOGSTAV, dbo.JY64800V.CELAV AS EJERLAVSKODE, dbo.JY64800V.EMATNR AS MATRIKELNUMMER, 
                         RTRIM(dbo.JY64800V.ABGSTM) AS MATRIKELBOGSTAV, RTRIM(dbo.JY67700V.LANDSEJERLAVSTEKST) AS LANDSEJERLAVSTEKST, RTRIM(dbo.JY64800V.EPCEL) 
                         AS PARCELNUMMER, RTRIM(CAST(dbo.QL61000V.TEKST AS VARCHAR(75))) AS ARTSKODE, dbo.JY64800V.FTOTAR AS MATRIKULAERT_AREAL, 
                         dbo.JY64800V.FTOTVEJ AS HERAF_VEJAREAL, dbo.JY64800V.FARVUR AS VURDERET_AREAL, dbo.JY64800V.FVEJVUR AS HERAF_VURDERET_VEJAREAL, 
                         dbo.JY64800V.CEJFORH AS EJERFORHOLDSKODE, RTRIM(CAST(dbo.QL50000V.TEKST AS VARCHAR(75))) AS EJERFORHOLD, 
                         dbo.JY64800V.FEJER AS ANTAL_EJERE, dbo.JY64800V.FLEJL AS ANTAL_LEJLIGHEDER, dbo.JY64800V.EMODER AS MODEREJENDOMSNUMMER, 
                         RTRIM(dbo.JY64800V.COFVEJ) AS ADGANG_TIL_OFFENTLIG_VEJ, RTRIM(dbo.JY64800V.CBSKADRA) AS ADMIN_ADRESSEBESKYTTELSE, 
                         RTRIM(dbo.JY64800V.AANAVN) AS ADMINISTRATORS_NAVN, RTRIM(dbo.JY64800V.AACONAVN) AS ADMINISTRATORS_CO_NAVN, RTRIM(dbo.JY64800V.AAADRESS) 
                         AS ADMINISTRATORS_ADRESSE, RTRIM(dbo.JY64800V.AAUDVADR) AS ADMINISTRATORS_UDV_ADRESSE, RTRIM(dbo.JY64800V.AAPOST) 
                         AS ADMINISTRATORS_POSTDISTRIKT, dbo.JY64800V.EADMNR AS ADMINISTRATORNUMMER, dbo.JY64800V.DCPRADM AS ADMINISTRATORS_CPR_CVR, 
                         RTRIM(dbo.JY64800V.CADM) AS ADMINISTRATORKODE, RTRIM(dbo.JY64800V.EJERLEJLIGHEDSNR) AS EJERLEJLIGHEDSNR, 
                         CAST(dbo.JY64900V.EKOMNR AS VARCHAR(3)) + RIGHT('0000' + CONVERT(VARCHAR, dbo.JY64800V.VEJ_KODE), 4) + RTRIM(dbo.JY64800V.HUS_NUMMER) 
                         AS ADRESSELINK_ID, RTRIM(CAST(CAST(dbo.JY64900V.EKOMNR AS VARCHAR(3)) + CAST(dbo.JY64800V.VEJ_KODE AS VARCHAR(4)) 
                         + CAST(dbo.JY64800V.HUS_NUMMER AS VARCHAR(4)) AS VARCHAR(14))) AS ADRESSE_ID, RTRIM(CAST(CAST(dbo.JY64900V.EKOMNR AS VARCHAR(3)) 
                         + CAST(dbo.JY64800V.VEJ_KODE AS VARCHAR(4)) + CAST(dbo.JY64800V.HUS_NUMMER AS VARCHAR(4)) + LTRIM(RTRIM(REPLACE(dbo.JY64800V.ELEJNR, ' ', ''))) 
                         AS VARCHAR(14))) AS UDVADRESSEID, RTRIM(CAST(dbo.JY67700V.LANDSEJERLAVSKODE AS VARCHAR(15)) + '' + CAST(dbo.JY64800V.EMATNR AS VARCHAR(10)) 
                         + dbo.JY64800V.ABGSTM) AS MATRIKELID
FROM            dbo.QL61500V RIGHT OUTER JOIN
                         dbo.QL61000V RIGHT OUTER JOIN
                         dbo.JY64800V ON dbo.QL61000V.KODE = dbo.JY64800V.CARTK ON dbo.QL61500V.KODE = dbo.JY64800V.CBENYT LEFT OUTER JOIN
                         dbo.JY67700V ON dbo.JY64800V.ABGSTM = dbo.JY67700V.MATRIKELBOGSTAV AND dbo.JY64800V.EMATNR = dbo.JY67700V.MATRIKELNR AND 
                         dbo.JY64800V.CELAV = dbo.JY67700V.EJERLAVSKODE RIGHT OUTER JOIN
                         dbo.QL63700V RIGHT OUTER JOIN
                         dbo.JY64900V ON dbo.QL63700V.KODE = dbo.JY64900V.COVMD LEFT OUTER JOIN
                         dbo.QL63400V ON dbo.JY64900V.CPENS = dbo.QL63400V.KODE LEFT OUTER JOIN
                         dbo.QL63200V ON dbo.JY64900V.CMSTAT = dbo.QL63200V.KODE LEFT OUTER JOIN
                         dbo.QL63500V ON dbo.JY64900V.CLEJE = dbo.QL63500V.KODE LEFT OUTER JOIN
                         dbo.QL50000V ON dbo.JY64900V.CEJFORH = dbo.QL50000V.KODE ON dbo.JY64800V.EEJDNR = dbo.JY64900V.EEJDNR LEFT OUTER JOIN
                         dbo.QL63600V ON dbo.JY64900V.CBSKADR = dbo.QL63600V.KODE