SELECT DISTINCT 
                         CAST(dbo.JY64300V.KOMMUNENUMMER AS VARCHAR(3)) + RIGHT('0000' + CONVERT(VARCHAR, dbo.JY64300V.VEJ_KODE), 4) 
                         + RTRIM(dbo.JY64300V.HUS_NUMMER) AS ADRESSELINK_ID, CAST(dbo.JY64300V.KOMMUNENUMMER AS VARCHAR(3)) 
                         + CAST(dbo.JY64300V.VEJ_KODE AS VARCHAR(4)) + RTRIM(dbo.JY64300V.HUS_NUMMER) AS ADRESSE_ID, dbo.JY64300V.VEJ_KODE, 
                         RTRIM(dbo.JY64300V.HUS_NUMMER) AS HUS_NUMMER, RTRIM(dbo.JY63200V.AVEJ) + ' ' + REPLACE(LTRIM(REPLACE(RTRIM(dbo.JY64300V.HUS_NUMMER), '0', 
                         ' ')), ' ', '0') AS ADRESSE
FROM            dbo.JY64300V LEFT OUTER JOIN
                         dbo.JY63200V ON dbo.JY64300V.VEJ_KODE = dbo.JY63200V.CVEJK LEFT OUTER JOIN
                         dbo.JY72000V ON dbo.JY64300V.KOMMUNENUMMER = dbo.JY72000V.KOMMUNENUMMER AND dbo.JY64300V.VEJ_KODE = dbo.JY72000V.VEJKODE AND 
                         dbo.JY64300V.HUS_NUMMER = dbo.JY72000V.HUS_NR
WHERE        (dbo.JY64300V.VEJ_KODE < 9000) AND (CAST(RTRIM(dbo.JY72000V.VEJADRNAVN + ' ' + REPLACE(LTRIM(REPLACE(RTRIM(dbo.JY72000V.HUS_NR), '0', ' ')), ' ', '0')) 
                         AS VARCHAR(50)) IS NULL)